import React from 'react';
import './HomeSlider.css';

class Slide extends React.Component {
    render() {
        let slideStyle = { backgroundImage: `url( ${this.props.background})` };
        return (
            <div
                className="slider__slide"
                data-active={this.props.active}
                style={slideStyle}
            >
                {/* <div className="slider__slide__text">
                    <h2>
                    {this.props.text}
                    </h2>
                </div> */}
            </div>
        );
    }
}

class HomeSlider extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            activeSlide: 0,
            interval: 3,
            autoplay: true,
            slides: [
                {
                    background: "/img/menu/kota.png",
                    // text: "Create your own Kota"
                },
                {
                    background: "/img/menu/rice.jpg",
                    // text: "Can we brighten your lunch with our 7 colors?"
                },
                {
                    background: "/img/header/cakes.jpg",
                    // text: "Craving some freshly baked muffins?"
                },
                {
                    background: "/img/header/ice_cream.jpg",
                    // text: "Needing a soft serve fix for your sweet tooth?"
                }
            ]
        };
    }

    componentDidMount() {
        this.timerId = setInterval(() => {
            console.log(this.state.autoplay);
            if (this.state.autoplay) {
                this.nextSlide();
            }
        }, this.state.interval * 1000);
    }

    componentWillUnmount() {
        clearInterval(this.timerId);
    }

    pause() {
        this.setState({ autoplay: false });
    }

    resume() {
        this.setState({ autoplay: true });
    }

    prevSlide() {
        let slide = this.state.activeSlide - 1 < 0
            ? this.state.slides.length - 1
            : this.state.activeSlide - 1;
        this.setState({
            activeSlide: slide
        });
    }

    nextSlide() {
        let slide = this.state.activeSlide + 1 < this.state.slides.length
            ? this.state.activeSlide + 1
            : 0;
        this.setState({
            activeSlide: slide
        });
    }

    render() {
        return (
            <div id='homeSlider' onMouseEnter={this.pause.bind(this)} onMouseLeave={this.resume.bind(this)}>
                {this.state.slides.map((slide, index) => {
                    return (
                        <Slide
                            background={slide.background}
                            text={slide.text}
                            active={index === this.state.activeSlide}
                        />
                    );
                })}
                <div className="rightArrow" onClick={this.nextSlide.bind(this)}><i className="fa fa-angle-double-right"></i></div>
                <div className="leftArrow" onClick={this.prevSlide.bind(this)}> <i className="fa fa-angle-double-left"></i></div>
            </div>
        );
    }
}

export default HomeSlider;
